import React from "react";

const MealSearch = ({meal}) => {

    console.log(meal);

    return (
        <Row className="mt-3">
            <h1 className="CategoriesTitle text-center">Meals for {categoryName}</h1>
                {
                    meals.map((meal) => {
                        return (
                            <Col key={meal.idMeal} xs={6} sm={6} md={4} lg={3} className="my-1">
                                    <div className="card">
                                        <img src={meal.strMealThumb} className="card__image" alt="hu"/>
                                        <div className="CardCategor">
                                            <div className="w-100 p-2">
                                                <p className="CategoryName">{meal.strMeal}</p>
                                            </div>
                                        </div>
                                    </div>
                            </Col>
                        )
                    })
                }
        </Row>
    )
}

export default MealSearch;
