import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/Header';
import Home from './components/Home/Home';
import CatigoriesList from './components/CatigoriesList'
import Meals from './components/meals';
import Contact from './components/contact';
import { Container } from 'react-bootstrap';
import {
  createBrowserRouter,
  createRoutesFromElements,
  RouterProvider,
  Route
} from "react-router-dom";

function App() {
  const [category, setCategory] = useState([]);
 //Get All Gategories
  const getCategory = async () => {
    const data = await axios.get('https://www.themealdb.com/api/json/v1/1/categories.php');
    setCategory(data.data.categories)
  }

  useEffect(() => {
    getCategory();
    },[])

    const router = createBrowserRouter(
      createRoutesFromElements(
        <Route path='/' element={<Header/>}>
          <Route index element={<CatigoriesList category={category}/>}/>
          <Route path='infoPetra' element={<Meals/>}/>
          <Route path='about' element={<About/>}/>
          <Route path='services' element={<Services/>}/>
          <Route path='pricing' element={<Pricing/>}/>
          <Route path='contact' element={<Contact/>}/>
        </Route>
      )
    )
  return (
    <>
      <Header />
      <Home/>
      <Container>
          <BrowserRouter>
            <Routes>
              <Route path='/' element={<CatigoriesList category={category}/>} />
              <Route path='/category/:categoryName' element={<Meals/>} />
            </Routes>
          </BrowserRouter>
      </Container>
    </>
  );
}

export default App;
