import React, {useState, useEffect} from "react";
import axios from "axios";
import { useParams } from "react-router";
import { Row, Col,Button,Card } from "react-bootstrap";
const MealDetailes = () => {
    const { mealId } = useParams();  // Extract mealId from the URL
    const [details, setdetails] = useState([]);  // State for storing meal details
  
    const getMealDetails = async () => {
      const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${mealId}`);
      console.log('API Response:', data.data);

      if (data.data.meals && data.data.meals.length > 0) {
        setdetails(data.data.meals[0]);
      }else{
        console.log('No meal found with ID:', mealId);
      }
    }
    useEffect(() => {
      getMealDetails();
    }, []);
  
    return (
      <Container>
      <Row className="my-5">
        <h1 className="CategoriesTitle text-center my-5 mb-3" >Meals for {categoryName}</h1>
            {
                meals.map((meal) => {
                    return (
                        <Col key={meal.idMeal} xs={6} sm={6} md={4} lg={3} className="my-1">
                                <Link to={`/category/meal/${meal.idMeal}`} className="text-decoration-none">
                                <div className="card">
                                    <LazyLoadImage src={meal.strMealThumb}
                                     className="card__image"
                                     effect="blur"
                                      alt="meal"/>
                                    <div className="CardCategor">
                                        <div className="w-100 p-2">
                                            <p className="CategoryName">{meal.strMeal}</p>
                                        </div>
                                    </div>
                                </div>
                                </Link>
                        </Col>
                    )
                })
            }
    </Row>
    </Container>
    );
}
export default MealDetailes;
