import Meals from "./Meals";
const Catigories = ({cat}) => {

    const [meals, setMeals] = useState([]);
    const getmeals = async () => {
        const meal = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${cat.strCategory}`);
        setMeals(meal.data.meals);
      }
    
    useEffect(() => {
        getmeals();
    },[])

    const filterbyCategorty = (Caty) =>{
          const newArr = meals.filter((item)=> item.strCategory === Caty)
          setMeals(newArr)
        
      }

      const onFilter = (Caty) => {
        filterbyCategorty(Caty)
      }


