import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/Header';
import Home from './components/Home/Home';
import CatigoriesList from './components/CatigoriesList'
import { Container } from 'react-bootstrap';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
function App() {
  const[category, setCategory] = useState([]);
  const [meals, setMeals] = useState([]);
 //Get All Gategories
  const getCategory = async () => {
    const data = await axios.get('https://www.themealdb.com/api/json/v1/1/categories.php');
    setCategory(data.data.categories);
    setMeals(data.data.meals);
  }

  //Get Meals By Category
  const getMealsByCategory = async (category) => {
    const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${category}`);
    setCategory(data.data.meals);
    setMeals(data.data.meals);
  }

  useEffect(() => {
    getCategory();
    },[])

  return (
    <>
      <Header />
      <Home/>
      <Container>
          <BrowserRouter>
            <Routes>
              <Route path='/' element={<CatigoriesList category={category} getMealsByCategory/>} />
              <Route path="/category/:name" element={<CatigoriesList/>} />
            </Routes>
          </BrowserRouter>
      </Container>
    </>
  );
}

export default App;
