import React, {useState, useEffect} from "react";
import axios from "axios";
import { useParams } from "react-router";
import { Row, Col,Container } from "react-bootstrap";
import {LazyLoadImage} from 'react-lazy-load-image-component';
const MealDetailes = () => {
    const { mealId } = useParams();  // Extract mealId from the URL
    const [details, setdetails] = useState([]);  // State for storing meal details
  
    const getMealDetails = async () => {
      const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${mealId}`);
      console.log('API Response:', data.data);

      if (data.data.meals && data.data.meals.length > 0) {
        setdetails(data.data.meals[0]);
      }else{
        console.log('No meal found with ID:', mealId);
      }
    }
    useEffect(() => {
      getMealDetails();
    }, []);

    const ingredients = [];
      for (let i = 1; i <= 20; i++) {
        const ingredient = details[`strIngredient${i}`];
        if (ingredient) {
          ingredients.push(ingredient);
        }
      }

      const measures = [];
        for (let i = 1; i <= 20; i++) {
          const measure = details[`strMeasure${i}`];
          if (measure && measure.trim() !== '') {
            measures.push(measure);
          }
        }
  
    return (
      <Container>
      <Row className="my-5">
      <h1 className="CategoriesTitle text-center my-5 mb-3" >{details.strMeal}</h1>
                        <Col xs={12} sm={6} md={6} lg={4} className="my-1">
                                <div className="card">
                                    <LazyLoadImage src={details.strMealThumb}
                                     className="card__image"
                                      effect="blur"
                                      alt="meal"/>
                                </div>
                                <hr/>
                        </Col>
                        <Col xs={12} sm={6} md={6} lg={8} className="my-1">
                            <div>
                            <h3 className="ingredients-title text-center mb-3">Ingredients</h3>
                                {
                                  ingredients.length > 0 ? ingredients.map((ingredient, index) => (
                                    <li key={index} className="badge bg-primary item-ingredient mx-2">
                                      {index + 1}- {ingredient}
                                    </li>
                                  )) : 'No ingredients'
                                }
                            </div>
                            <div className="my-5">
                              <h3 className="tag-title text-center mb-3">Measure</h3>
                              <p>
                                {
                                  measures.length > 0 ? measures.map((measure, index) => (
                                    <li key={index} className="item-tag badge bg-primary mx-2">	&#x1F944; {measure}</li>
                                  )) : 'No measures available'
                                }
                              </p>
                          </div>
                          <div className="my-5">
                          <p>
                            <h3 className="tag-title text-center mb-3">Tags</h3>
                            {
                              details.strTags ? details.strTags.split(',').map((tag, index) => (
                              <span key={index} className="item-tag badge bg-primary mx-2">&#x25AA;	{tag}</span>
                              )) : <></>
                            }
                          </p>
                          </div>
                        </Col>
                        <Col>
                          <div className="strInstructions w-100 p-2">
                            <h3 className="mb-3 ">Instructions</h3>
                            <p>
                              {
                                details.strInstructions
                              }
                            </p>
                          </div>
                          <div className="mx-2">
                              <a className="btn btn-primary mx-2" href={details.strSource} target="_blank" rel="noopener noreferrer">Meal Source</a>
                              <a className="btn btn-primary mx-2" href={details.strYoutube} target="_blank" rel="noopener noreferrer">Youtube Video</a>
                          </div>
                        </Col>
    </Row>
    </Container>
    );
}
export default MealDetailes;
