import React, { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import { Row, Col } from "react-bootstrap";
const Meals = ({category}) => {

    const [meals, setMeals] = useState([]);
    const getmeals = async (cat) => {
        const meal = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${cat.strCategory}`);
        setMeals(meal.data.meals);
        console.log(meal.data.meals);
    }

    return(
        <div>
            {
                category.map((meal)=>{
                    return(
                        <Col xs={6} sm={6} md={4} lg={3} className="my-1" key={meal.idMeal}>
                            <div className="card"></div>
                                <img src={meal.strMealThumb} className="card__image" alt="hu"/>
                        </Col>
                    )
            })
            }
        </div>
    )
}

export default Meals;
