import React, { useEffect, useState } from "react";
import axios from "axios";
import { Col, Row } from "react-bootstrap";
import MealsDetailse from "./MealsDetailse";
const Meals = ({category}) => {
    const [meals, setmeals] = useState([]);

    const getMeals = async () => {
        const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${category.idCategory}`);
        setmeals(data.data.categories)
    }

    useEffect(() => {
        getMeals();
    },[])
    return(
        <Row className="mt-3">
        <h1 className="CategoriesTitle text-center">categories</h1>
       {
        category.map((cat)=>{
            return(
                <MealsDetailse cat={cat} key={cat.strMeal}/>
            )
            
        })
       }
    </Row>
    )
}

export default Meals;
