import React, { useEffect, useState } from "react";
import axios from "axios";
import { Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import Meals from "./Meals";
const Catigories = ({cat}) => {

    const [meals, setMeals] = useState([]);
    const getmeals = async () => {
        const meal = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${cat.strCategory}`);
        setMeals(meal.data.meals);
        console.log(meal.data.meals);
      }
    
    useEffect(() => {
        getmeals();
    },[])
    return(
            <Col xs={6} sm={6} md={4} lg={3} className="my-1">
            
            <Link to={`meals/category/${cat.strCategory}`} className="text-decoration-none">
            <div className="card"
                onClick={() =>  meals.map((mealarr)=>{
                        return(
                            <Meals mealarr={mealarr} key={mealarr.idMeal} />
                        )
                    })
                }
            >
                    <img src={cat.strCategoryThumb} className="card__image" alt="hu"/>
                    <div className="CardCategor">
                        <div className="w-100 p-2">
                            <p className="CategoryName">{cat.strCategory}</p>
                        </div>
                    </div>
            </div>
            </Link>
            </Col>
            /*<div className="card">
                    <img src={cat.strCategoryThumb} className="card__image" alt="hu"/>
                    <div className="CardCategor">
                        <div className="w-100 p-2">
                            <p className="CategoryName">{cat.strCategory}</p>
                        </div>
                    </div>
            </div>*/
            
    )
}
export default Catigories;
