import React, { useEffect, useState } from "react";
import axios from "axios";
const MealsDetailse = ({cat}) => {
    
    const [meals, setMeals] = useState([]);
    const getmeals = async () => {
        const meal = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${cat.strCategory}`);
        setMeals(meal.data.meals)
        console.log(meal.data.meals)
      }
    
    useEffect(() => {
        getmeals();
    },[])

    return(
        <div>
            <div className="card">
                    <img src={cat.strMealThumb} className="card__image" alt="hu"/>
                    <div className="CardCategor">
                        <div className="w-100 p-2">
                            <p className="CategoryName">{cat.strMeal}</p>
                        </div>
                    </div>
            </div>
        </div>
    )
}

export default MealsDetailse
