import React, {useState, useEffect} from "react";
import axios from "axios";
import { useParams } from "react-router";
import { Row, Col,Button,Card } from "react-bootstrap";
const MealDetailes = () => {
    const { mealId } = useParams();  // Extract mealId from the URL
    const [details, setdetails] = useState([]);  // State for storing meal details
  
    const getMealDetails = async () => {
      const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${mealId}`);
      console.log('API Response:', data.data);

      if (data.data.meals && data.data.meals.length > 0) {
        setdetails(data.data.meals[0]);
      }else{
        console.log('No meal found with ID:', mealId);
      }
    }
    useEffect(() => {
      getMealDetails();
    }, []);
  
    return (
            <div className="MealDetailes my-5">
              <Row>
                  <Col sm={12} md={6} lg={6} className="my-5">
                      <Card style={{ width: '18rem'}}>
                      <img variant="top" src={details.strMealThumb}  />
                      <Card.Body>
                        <Card.Title>{details.strMeal}</Card.Title>
                        <Card.Text>
                          {
                            details.strTags && details.strTags.split(',').map((tag, index) => (
                              <span key={index} className="badge rounded-pill bg-primary me-1">{tag}</span>
                            ))
                          }
                        </Card.Text>
                        <Button>
                          <a href={details.strYoutube} className="btn btn-primary" target="_blank" rel="noopener noreferrer">Watch on YouTube</a>
                        </Button>
                      </Card.Body>
                    </Card>
                  </Col>
                  <Col sm={12} md={6} lg={6}>
                  <div className="instruction my-5">
                    <h5 className="card-text-details">Instructions:</h5>
                    <p className="card-text-details">{details.strInstructions}</p>
                  </div>
                </Col>
              </Row>
            </div>
    );
}
export default MealDetailes;
