import React, { lazy, Suspense, useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/Header';
import Home from './components/Home';
import CatigoriesList from './components/CatigoriesList';
import Meals from './components/meals';
import Contact from './components/Contact';
import FooterII from './components/FooterII';
import { Route, Routes} from "react-router-dom";
import LoaderCategories from './components/loader/LoaderCategories';

const HeaderPage = lazy(() => delayForDemo(import('./components/Header')));
const HomePage = lazy(() => delayForDemo(import('./components/Home')));
const CatigoriesListPage = lazy(() => delayForDemo(import('./components/CatigoriesList')));
const MealsCat = lazy(() => delayForDemo(import('./components/meals')));
const ContactPage = lazy(() => delayForDemo(import('./components/Contact')));
function App() {
  const [category, setCategory] = useState([]);

 //Get All Gategories
  const getCategory = async () => {
    const data = await axios.get('https://www.themealdb.com/api/json/v1/1/categories.php');
    setCategory(data.data.categories)
  }

  useEffect(() => {
    getCategory();
    },[])
    const search = async (word) =>{
      if(word === ""){
        getCategory();
      }else{
      const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${word}`);
      setCategory(data.data.meals)
      }
    }
  return (

      <>
        <Header />
        <Suspense fallback={<LoaderCategories/>}>

                      <Routes>
                <Route path='/' element={
                  <>
                    <HomePage search={search} />
                    <CatigoriesListPage category={category} />
                  </>
                } />

                <Route path='/category/:categoryName' element={
                  <>
                    <MealsCat />
                  </>
                } />

                <Route path='/contact' element={
                  <>
                    <ContactPage />
                  </>
                } />
              </Routes>
        </Suspense>

 <FooterII/>
      </>
  );
}

function delayForDemo(promise) {
  return new Promise(resolve => {
    setTimeout(resolve, 2000);
  }).then(() => promise);
}
export default App;
