import React, {useState, useEffect} from "react";
import axios from "axios";
import { useParams } from "react-router";
const MealDetailes = () => {
    const { mealId } = useParams();  // Extract mealId from the URL
    const [meal, setMeal] = useState(null);  // State for storing meal details
    const [loading, setLoading] = useState(true);  // State for loading status
    const [error, setError] = useState('');  // State for handling errors
  
    useEffect(() => {
      const fetchMealDetails = async () => {
        try {
          const response = await axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${mealId}`);
  
          console.log('API Response:', response.data);  // Log the entire response for debugging
  
          if (response.data.meals && response.data.meals.length > 0) {
            setMeal(response.data.meals[0]);  // Set the first meal in the state
            setLoading(false);  // Stop loading
          } else {
            setError('No meal found for this ID');  // Handle if no meal found
            setLoading(false);
          }
        } catch (error) {
          console.error('Error fetching meal data:', error);
          setError('An error occurred while fetching meal data');
          setLoading(false);
        }
      };
  
      fetchMealDetails();  // Fetch meal details when the component mounts
    }, [mealId]);  // Re-run the effect when mealId changes
  
    // Conditional rendering based on loading, error, and meal data
    return (
      <div>
        {loading && <p>Loading meal details...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Display any error */}
        {meal && (
          <div>
            <h2>{meal.strMeal}</h2>
            <img src={meal.strMealThumb} alt={meal.strMeal} />
            <p>{meal.strInstructions}</p>
            {/* Add more meal details if necessary */}
          </div>
        )}
      </div>
    );
}

export default MealDetailes;
