import React, {useState, useEffect} from "react";
import axios from "axios";
import { useParams } from "react-router";
import { Row, Col, Container } from "react-bootstrap";
const MealDetailes = () => {
    const { mealId } = useParams();  // Extract mealId from the URL
    const [details, setdetails] = useState([]);  // State for storing meal details
  
    const getMealDetails = async () => {
      const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${mealId}`);
      console.log('API Response:', data.data);

      if (data.data.meals && data.data.meals.length > 0) {
        setdetails(data.data.meals[0]);
      }else{
        console.log('No meal found with ID:', mealId);
      }
    }
    useEffect(() => {
      getMealDetails();
    }, []);

    const ingredientsArr = [];
  if(details && details.length > 0){
    for(let props in details[0]){
      if(props.includes('strIngredient')){
        if(details[0][props]) ingredientsArr.push(details[0][props]);
      }
    }
  }
  
    return (
      <div>
            <div>
              <Row>
                  <Col md="12" xs="12" sm="12"  className="mt-4">
                    <div className="MealDetailes d-flex my-5">
                      <img src={details.strMealThumb} className="details-img" alt="img" />
                      <div className="justify-content-center text-center mx-auto">
                        <h2 className="meal-detailes">{details.strMeal}</h2>
                        <h4 className="card-text-details">Category: {details.strCategory}</h4>
                        <div>
                          {
                            details.strTags && details.strTags.split(',').map((tag, index) => (
                              <span key={index} className="badge rounded-pill bg-secondary mx-1">{tag}</span>
                            ))
                          }
                        </div>
                        <div>
                        {
                            meal?.ingredients?.map((ingredient, idx) => (
                              <li key = {idx} className = "flex align-center">
                                <span className='li-dot'>{idx + 1}</span>
                                <span className='text-capitalize text-white fs-15'>{ingredient}</span>
                              </li>
                            ))
                         }
                        </div>
                      </div>
                    </div>
                  </Col>
              </Row>
            </div>
      </div>
    );
}
export default MealDetailes;
