import React, { useEffect, useState } from "react";
import axios from "axios";
import { Col, Row } from "react-bootstrap";
const MealsDetailse = ({cat}) => {
    const [meals, setmeals] = useState([]);

    const getMeals = async () => {
        const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${cat.strCategory}`);
        setmeals(data.data.categories)
    }

    useEffect(() => {
        getMeals();
    }, []);

    return(
        <div>
            <div className="card">
            {
                cat.strCategory.map((cat) => {
                return(
                    <div className="card">
                        <img src={cat.strCategoryThumb} className="card__image" alt="hu"/>
                        <div className="CardCategor">
                            <div className="w-100 p-2">
                                <p className="CategoryName">{cat.stMeal}</p>
                            </div>
                        </div>
                    </div>
                )
            })}
            </div>
            
        </div>
            
    )
}

export default MealsDetailse
