import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/Header';
import Home from './components/Home/Home';
import CatigoriesList from './components/CatigoriesList'
import Meals from './components/Meals';
import { Container } from 'react-bootstrap';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
function App() {
  const[category, setCategory] = useState([]);
  const [meals, setMeals] = useState([]);

 //Get All Gategories
  const getCategory = async () => {
    const data = await axios.get('https://www.themealdb.com/api/json/v1/1/categories.php');
    setCategory(data.data.categories);
  }

  const getmeals = async (categ) => {
    const meal = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${category.strCategory}`);
    setCategory(meal.data.categories);
    setMeals(meal.data.meals);
    console.log(meal.data.meals);
  }

  useEffect(() => {
    getCategory();
    getmeals();
    },[])

    const filterbycategory = (categ) => {
      if(categ !== ""){
        const Filmeals = meals.filter((meal) => meal.strCategory === categ)
        setMeals(Filmeals)
    }
  }
  return (
    <>
      <Header />
      <Home/>
      <Meals/>
      <Container>
          <BrowserRouter>
            <Routes>
              <Route path='/' element={<CatigoriesList filterbycategory={filterbycategory} category={category}/>} />
              <Route path='/meals/:idMeal' element={<Meals meals={meals}/>} />
            </Routes>
          </BrowserRouter>
      </Container>
    </>
  );
}

export default App;
