import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/Header';
import Home from './components/Home';
import CatigoriesList from './components/CatigoriesList'
import Meals from './components/meals';
import Contact from './components/Contact';
import { Container } from 'react-bootstrap';
import { Route, Routes} from "react-router-dom";
function App() {
  const [category, setCategory] = useState([]);
  const [movies, setMovies] = useState([]);
 //Get All Gategories
  const getCategory = async () => {
    const data = await axios.get('https://www.themealdb.com/api/json/v1/1/categories.php');
    setCategory(data.data.categories)
  }

  useEffect(() => {
    getCategory();
    },[])
    const search = async (word) =>{
      if(word === ""){
        getCategory();
      }else{
      const res = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=f2f548611cb9790f65fd95c721c4385a&query=${word}`)
        setMovies(res.data.results)
        setPageCount(res.data.total_pages)
      }
    }
  return (

   <>
      <Header />

            <Routes>
              <Route path='/' element={<CatigoriesList category={category}/>} />
              <Route path='/category/:categoryName' element={<Meals/>} />
              <Route path='contact' element={<Contact/>} />
            </Routes>

    </>
  );
}

export default App;
