import React, {useState, useEffect} from "react";
import axios from "axios";
import { useParams } from "react-router";
const MealDetailes = () => {
    const [details, setDetails] = useState([]);
    
    const {IdMeal} = useParams();
    const getDetails = async () => {
        const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${IdMeal}`);
        setDetails(data.data.meals[52772]);
        console.log(data.data.meals[52772]);
    }

    useEffect(() => {
        if(IdMeal){
            getDetails();
        }
    },[])

    return(
        <div>
        <h1>hhhhhhhhhh</h1>
        <h1>hhhhhhhhhh</h1>
        <h1>hhhhhhhhhh</h1>
        <h1>hhhhhhhhhh</h1>
        <h1>hhhhhhhhhh</h1>
        <h1>hhhhhhhhhh</h1>
        <h1>hhhhhhhhhh</h1>
      {
        details.map((detail) => {
          return (
            <div>
          <h2>{detail.strMeal}</h2>
          <img src={detail.strMealThumb} alt={detail.strMeal} />
          <p>{detail.strInstructions}</p>
          {/* Render more meal details as needed */}
        </div>
          )
        })
      }

    </div>
    )
}

export default MealDetailes;
