import React, { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import { Row, Col } from "react-bootstrap";
import Catigories from "./Catigories";
const Meals = ({category}) => {
    const [meals, setMeals] = useState([]);

    const getMealsByCategory = async (strCategory) => {
        const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${strCategory}`);
        setMeals(data.meals);
      }

      useEffect(() => {
        getMealsByCategory();
      }, []);
    return(
        <Row className="mt-3">
            <h1 className="text-center">{category.strCategory}</h1>
            <p>{category.strCategoryDescription}</p>
            {
                meals.map((meal)=>{
                    return(
                        <Col  xs={6} sm={6} md={4} lg={3} className="my-1">
                        <div className="card">
                            <img src={meal.strMealThumb} className="card__image" alt="hu"/>
                            <div className="CardCategor">
                            <div className="w-100 p-2">
                                <p>{meal.strMeal}</p>
                                <p>{meal.strArea}</p>
                                <p>{meal.strMeal}</p>
                            </div>
                            </div>
                        </div>
                            <p>{meal.strMeal}</p>
                        </Col>
                    )
                })
           }
        </Row>
    )
}

export default Meals;
