import React,{useState, useEffect} from "react";
import axios from "axios";
import { Container } from "react-bootstrap";
import MealSearch from "./MealSearch";

const Search = () => {
    const [mymeals, setMeals] = useState([]);

    const getMeals = async (name) => {
        const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${name}`);
        setMeals(data.data.meals);
    }

    useEffect(() => {
        getMeals();
    },[])

    return(
        <Col>
        <div className="form-control">
        <input className="input input-alt" placeholder="Search..." type="text" onChange={(e) => getMeals(e.target.value)}/>
        <span className="input-border input-border-alt"></span>

        <>
            {
                (mymeals==null)? <p>Not Found</p> :
                mymeals.map((meal) => {
                    return(
                        <MealSearch key={meal.idMeal} meal={meal}/>
                    )
                        
                })
            }
        </>
    </div>
    </Col>

    )
}
export default Search;
