import React, { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
const Meals = () => {
    const [meals, setMeals] = useState([]);

    const getMealsByCategory = async (meals) => {
        const data = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${meals}`);
        setMeals(data.meals);
      }

      useEffect(() => {
        getMealsByCategory();
      }, []);
    return(
        <h1>meals</h1>
    )
}

export default Meals;
