import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/Header';
import Home from './components/Home/Home';
import CatigoriesList from './components/CatigoriesList'
import Meals from './components/Meals';
import { Container } from 'react-bootstrap';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
function App() {
  const [category, setCategory] = useState([]);
  const [activeCategory, setactiveCategory] = useState("")
  const [meals, setMeals] = useState([]);

 //Get All Gategories
  const getCategory = async () => {
    const data = await axios.get('https://www.themealdb.com/api/json/v1/1/categories.php');
    setCategory(data.data.categories)
  }

  const getmeals = async (category) => {
    const meal = await axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${category}`);
    setMeals(meal.data.meals);
  }

  useEffect(() => {
    getCategory();
    getmeals();
    },[])

  return (
    <>
      <Header />
      <Home/>
      <Meals meals={meals} />
      <Container>
          <BrowserRouter>
            <Routes>
              <Route path='/' element={<CatigoriesList category={category}/>} />
              <Route path='/meals/:idMeal' element={<Meals meals={meals}/>} />
            </Routes>
          </BrowserRouter>
      </Container>
    </>
  );
}

export default App;
