import React from "react";
import { Container } from "react-bootstrap";
import { Link } from "react-router-dom";

const MealSearch = ({category,search}) => {
    const onSearch = (e) => {
        search(e.target.value)
    }
    return(
        <Container>
            <h1>Meal Search</h1>
            <input type="text" className="form-control" onChange={onSearch}/>
            {
                category.map((cat)=>{
                    return(
                        <div key={cat.idCategory}>
                            <Link to={`/mealdetails/${cat.idCategory}`}>
                            <div className="card" >
                                    <img src= {cat.strCategoryThumb || cat.strMealThumb} className="card__image" alt="hu"/>
                                    <div className="CardCategor">
                                        <div className="w-100 p-2">
                                            <p className="CategoryName">{cat.strMeal || cat.strCategory}</p>
                                        </div>
                                    </div>
                            </div>
                            </Link>
                        </div>
                    )
                })
            }
        </Container>
    )
}
export default MealSearch
