import React, { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import { Row, Col } from "react-bootstrap";
import { useParams } from "react-router-dom";
const Meals = ({meals,getmeals,setMeals}) => {
    
    const getmeal = () =>{
        axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${meals}`)
        .then((res)=>{
            setMeals(res.data.meals);
        })
    }

    return(
        <div>
            {
                meals.map((meal)=>{
                    return(
                        <Col xs={6} sm={6} md={4} lg={3} className="my-1" key={meal.idMeal}>
                            <div className="card"></div>
                                <img src={meal.strMealThumb} className="card__image" alt="hu"/>
                        </Col>
                    )
            })
            }
        </div>
    )
}

export default Meals;
